"""
gaussian fits to sersic profiles

These are tabulated using the code from Hogg & Lang
"""
from __future__ import print_function
import numpy
from numpy import array
import os
from glob import glob

def print_sersic_fits(ngauss=10):
    """
    filenames are like 1.47840165_K10_MR08.txt
    """
    dir=os.path.join(
        os.environ['HOME'],
        'git/TheTractor/code',
    )

    pattern='*_K%02d_MR08.txt' % ngauss
    pattern=os.path.join(dir, pattern)

    flist=glob(pattern)

    num=len(flist)

    dt=[
        ('n','f8'),
        ('fp','f8',2*ngauss),
    ]
    data = numpy.zeros(num, dtype=dt)

    for i,f in enumerate(flist):
        bname=os.path.basename(f)

        parts=bname.split('_')

        data['n'][i] = float(parts[0])
        
        with open(f) as fobj:
            p_and_v_string =fobj.read().strip()
            p_and_v = eval(p_and_v_string)

            #raw_f = p_and_v[0:ngauss]
            #raw_p = p_and_v[ngauss:]
            raw_p = p_and_v[0:ngauss]
            raw_f = p_and_v[ngauss:]

            p = raw_p/raw_p.sum()
            fnorm = (raw_f*p).sum()
            f = raw_f/fnorm

            data['fp'][i,0:ngauss] = f
            data['fp'][i,ngauss:] = p

    s = data['n'].argsort()
    data=data[s]
    print(repr(data))

